import React, { useState } from 'react'
import { useParams } from 'react-router-dom';

function DetailPage() {
    const {id} =useParams();
    const [user,setUser]=useState({})
  
    useEffect(() => {
        axios.get(`http://localhost:8080/Polo_shirt/${id}`)
       .then((res)=>{
         setUser(res.data)
         console.log(res.data)
       })}, [])
  return (
      <div>
        { 
        <p>{user.name}</p>
        }
    </div>
  )
}

export default DetailPage
